INSERT INTO creatures (name, latin_name, category, description, habitat, diet, size_info, lifespan, conservation_status, image_url, premium_content) VALUES
('Бабочка Монарх', 'Danaus plexippus', 'Насекомые', 'Крупная бабочка с характерной оранжево-черной окраской. Известна своими удивительными миграциями на расстояние до 4000 км.', 'Луга, поля, сады Северной и Южной Америки', 'Личинки питаются исключительно молочаем, взрослые особи - нектаром цветов', 'Размах крыльев 8.9-10.2 см', '2-6 недель для взрослой особи', 'Под угрозой', '', 'Монархи используют магнитное поле Земли для навигации. Один экземпляр может пролететь до 5000 км во время миграции. Содержат токсины от молочая, делающие их несъедобными для хищников.'),
('Рыжий муравей', 'Formica rufa', 'Насекомые', 'Крупный вид муравьев, строящий большие муравейники в лесах. Важный хищник вредителей леса.', 'Хвойные и смешанные леса Европы и Азии', 'Хищники - охотятся на насекомых, также питаются медвяной росой тлей', 'Длина рабочего муравья 4-9 мм, матки до 11 мм', 'Рабочие до 3 лет, матки до 20 лет', 'Стабильная', '', 'Колония может содержать до 400000 особей. За день колония уничтожает до 100000 вредителей. Способны выбрызгивать муравьиную кислоту на расстояние до 30 см для защиты.'),
('Африканский слон', 'Loxodonta africana', 'Млекопитающие', 'Крупнейшее сухопутное млекопитающее планеты. Обладает высоким интеллектом и сложной социальной структурой.', 'Саванны, леса и пустыни Африки южнее Сахары', 'Травоядные - питаются травой, листьями, корой, плодами. Потребляют до 150 кг пищи в день', 'Высота в холке 3-4 м, вес 4000-7000 кг', '60-70 лет', 'Уязвимый', '', 'Обладают самым большим мозгом среди наземных животных (5 кг). Способны узнавать себя в зеркале. Общаются инфразвуком на расстоянии до 10 км. Период беременности 22 месяца - самый длинный среди млекопитающих.'),
('Императорский пингвин', 'Aptenodytes forsteri', 'Птицы', 'Самый крупный и тяжелый из пингвинов. Единственный вид, размножающийся в антарктическую зиму.', 'Прибрежные льды Антарктиды', 'Рыба, кальмары, криль', 'Рост до 130 см, вес 22-45 кг', '15-20 лет', 'Близкий к уязвимому', '', 'Могут нырять на глубину до 565 метров и задерживать дыхание до 22 минут. Самцы высиживают яйца 64 дня без пищи при температуре до -40°C. Теряют до 45% массы тела за период высиживания.'),
('Комодский варан', 'Varanus komodoensis', 'Рептилии', 'Крупнейшая современная ящерица. Опасный хищник с токсичной слюной.', 'Острова Комодо, Ринка, Флорес в Индонезии', 'Плотоядные - охотятся на оленей, кабанов, буйволов, падаль', 'Длина до 3 м, вес до 90 кг', '30-50 лет', 'Уязвимый', '', 'Способны проглатывать добычу размером до 80% собственного веса. Их слюна содержит более 50 видов бактерий. Обладают партеногенезом - самки могут размножаться без самцов. Скорость бега до 20 км/ч на коротких дистанциях.'),
('Медоносная пчела', 'Apis mellifera', 'Насекомые', 'Общественное насекомое, производящее мед. Важнейший опылитель сельскохозяйственных культур.', 'Распространена по всему миру, кроме Антарктиды', 'Нектар и пыльца цветов', 'Длина рабочей пчелы 12-15 мм', 'Рабочие 6 недель, матка 3-5 лет', 'Стабильная', '', 'Для производства 1 кг меда пчелы посещают 4 миллиона цветков. Пчелиная семья может содержать до 80000 особей. Пчелы танцуют, чтобы сообщить о местоположении цветов. Взмах крыльев 200 раз в секунду.'),
('Синий кит', 'Balaenoptera musculus', 'Млекопитающие', 'Крупнейшее животное за всю историю Земли. Может достигать размеров более 30 метров.', 'Все океаны мира', 'Криль - крошечные ракообразные, до 4 тонн криля в день', 'Длина до 33 м, вес до 200 тонн', '80-90 лет', 'Вымирающий', '', 'Сердце весит около 600 кг - размером с небольшой автомобиль. Новорожденный детеныш весит 2.5 тонны. Издают звуки громкостью до 188 децибел - самые громкие среди животных. Могут общаться на расстоянии до 1600 км под водой.'),
('Павлиний паук', 'Maratus volans', 'Паукообразные', 'Крошечный паук с яркой радужной окраской. Самцы исполняют сложные брачные танцы.', 'Леса и луга Австралии', 'Мелкие насекомые', 'Длина тела 4-5 мм', 'Около 1 года', 'Не оценивался', '', 'Обладают одним из самых сложных брачных танцев в природе. Окраска создается не пигментами, а микроскопическими чешуйками, отражающими свет. У самцов есть специальные "лопасти" по бокам брюшка для танца.'),
('Гигантская панда', 'Ailuropoda melanoleuca', 'Млекопитающие', 'Символ охраны природы. Питается почти исключительно бамбуком несмотря на пищеварительную систему хищника.', 'Горные леса центрального Китая', 'Бамбук (99% рациона), иногда мелкие животные и рыба', 'Длина 1.2-1.9 м, вес 70-125 кг', '15-20 лет в природе', 'Уязвимый', '', 'Должны съедать 12-38 кг бамбука ежедневно из-за низкой питательной ценности. Имеют "ложный большой палец" - увеличенную кость запястья для захвата бамбука. Новорожденные весят всего 100 грамм - в 900 раз меньше матери.'),
('Колибри-пчелка', 'Mellisuga helenae', 'Птицы', 'Самая маленькая птица в мире. Эндемик Кубы.', 'Леса и сады Кубы', 'Нектар цветов, мелкие насекомые', 'Длина 5-6 см, вес 1.6-2 грамма', '7-10 лет', 'Близкий к уязвимому', '', 'Сердце бьется 1200 раз в минуту в полете. Крылья делают до 80 взмахов в секунду. Температура тела падает с 40°C до 19°C во время ночного оцепенения для экономии энергии. Могут летать задом наперед и зависать на месте.'),
('Жук-геркулес', 'Dynastes hercules', 'Насекомые', 'Один из крупнейших жуков в мире с впечатляющим рогом самцов.', 'Тропические леса Центральной и Южной Америки', 'Личинки - гниющая древесина, взрослые - сок деревьев, гниющие фрукты', 'Длина до 17 см с рогом', 'Личинка 1-2 года, взрослый жук 3-6 месяцев', 'Не оценивался', '', 'Способны поднимать вес в 850 раз больше собственного. Рог используется в битвах самцов за самок. Окраска элитр меняется от желто-зеленого до черного в зависимости от влажности. Личинка может весить до 120 грамм.');
